import osimport shutilatlas_dir = '/nfs/masi/hansencb/t1_tract_data/Atlases'methods = ['AFQ', 'AFQclipped', 'Recobundles', 'TractSeg', 'Xtract', 'Tracula']for method in methods:    method_dir = os.path.join(atlas_dir, method+'Atlases')    tract_names_path = os.path.join(method_dir, 'tract_names.csv')    info_paths = [os.path.join(method_dir, method+'Linear_info.csv'),                  os.path.join(method_dir, method+'Nonlinear_info.csv')]    abbrev_to_name = {}    with open(tract_names_path, 'r') as f:        for line in f.readlines():            abbrev, name = line.strip().split(',')            abbrev_to_name[abbrev] = name    for path in info_paths:        with open(path, 'r') as f:            lines = f.readlines()        for i in range(1,len(lines)):            parts = lines[i].split(',')            parts[1] = abbrev_to_name[parts[1]]            lines[i] = ','.join(parts)        shutil.move(path, path+'.bak')        with open(path, 'w') as f:            for line in lines:                f.write(line)    vu_paths = [os.path.join(method_dir, method+'Linear_NORMAL.nii.gz'),                  os.path.join(method_dir, method+'Nonlinear_NORMAL.nii.gz')]    for path in vu_paths:        if os.path.isfile(path):            shutil.move(path, path.replace('NORMAL', 'VU'))